(function(payload,originator,done) {

  console.log()

  // FORMAT COMPANY
  let companies = ''
  if(Array.isArray(payload.params.companies)) {
    companies = payload.params.companies.map(c => c.value.toLowerCase().trim())
  }

  request.get(`https://availy.dazzletest.nl/Api/Location/?Lat=1&Lon=1&district=&quarter=&poi=&facilities=`)
    .then((result) => {

      const {
        status,
        data
      } = result

      if (!data.length) {
        const text = new Text(`Oops! Ik heb niets kunnen vinden ðŸ˜•`)
        text.addQuickReply(new QuickReply({ label: 'Opnieuw zoeken', value: 'Get started' }))
        const message = new Message(`Oops! Ik heb niets kunnen vinden ðŸ˜•`)
        message.addResponse(text)

        return reply(message)
      }

      // Create a list for movie titles
      const locations = []

      // Create a card carousel
      const carousel = new Carousel()

      data.forEach((location, idx) => {

        if(idx >= 300) {
          // Just 10 location, skip the other
          return
        }

        if(!companies.find(c => c === location.Name.toLowerCase().trim())) {
          return
        }

        // Add the title to the list
        locations.push(location.Name)

        var imageURL = ``
        var newchar = ``

        if (location.ImageUrls.length !== 0) {
          imageURL = `https://availy.dazzletest.nl`+location.ImageUrls
          imageURL = imageURL.split('~').join(newchar)
        } else {
          imageURL = `https://webhook-availy.herokuapp.com/images/result-placeholder.jpg`
        }

        const buttonMoreInfo = new Button({
          type: 'webview',
          label: 'Bekijk details',
          value: `https://webhook-availy.herokuapp.com/location/${location.Id}`
        })

        const buttonRouteInfo = new Button({
          type: 'webview',
          label: 'Toon route',
          value: `https://www.google.com/maps/dir//${location.Address}, ${location.Zipcode}, ${location.City}`
        })

        const buttonLike = new Button({
          type: 'postback',
          label: 'Leuk, ik ga erheen!',
          value: `Leuk, ik ga naar ${location.Name}`
        })

        // Create a location card
        const card = new Card({
          title: location.Name,
          subtitle: location.Address,
          media: new Media({
            type: 'image',
            url: imageURL
          })
        })

        .addButton(buttonMoreInfo)
        .addButton(buttonRouteInfo)
        .addButton(buttonLike)

        // Add the card to the carousel
        carousel.addCard(card)
      })

      if (!locations.length) {
        const text = new Text(`Oops! Ik heb niets kunnen vinden ðŸ˜•`)
        text.addQuickReply(new QuickReply({ label: 'Opnieuw zoeken', value: 'Get started' }))
        const message = new Message(`Oops! Ik heb niets kunnen vinden ðŸ˜•`)
        message.addResponse(text)

        return reply(message)
      }

      // Create a message with fallback speech
      const message = new Message(`The most popular are ${locations.join(', ')}`)

      message.addResponse(carousel)
      reply(message)
    })

    .catch((err) => {
      // This is not good..
      reply(new Message(err.message))
    })
})

